import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

function Home() {
  return (
    <div className="min-h-screen bg-gradient-to-b from-green-100 to-green-300 flex flex-col items-center justify-center text-center px-6">
      {/* Animated Title */}
      <motion.h1
        className="text-5xl md:text-6xl font-extrabold text-green-800 mb-6"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        ðŸŒ± Multi-Crop Plant Recognition
      </motion.h1>

      {/* Subtitle */}
      <motion.p
        className="text-lg md:text-2xl text-green-700 max-w-2xl mb-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        Upload plant images, identify species instantly, and grow your knowledge.
      </motion.p>

      {/* Buttons */}
      <motion.div
        className="flex gap-6"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1, duration: 1 }}
      >
        <Link
          to="/upload"
          className="bg-green-600 text-white px-6 py-3 rounded-xl shadow-lg hover:bg-green-700 transition"
        >
          Upload & Predict
        </Link>
        <Link
          to="/register"
          className="bg-white text-green-700 border border-green-600 px-6 py-3 rounded-xl shadow-lg hover:bg-green-100 transition"
        >
          Register
        </Link>
      </motion.div>

      {/* Floating Plant Illustration */}
      <motion.img
        src="https://cdn-icons-png.flaticon.com/512/765/765521.png"
        alt="Plant"
        className="w-40 mt-12"
        animate={{ y: [0, -20, 0] }}
        transition={{ repeat: Infinity, duration: 3 }}
      />
    </div>
  );
}

export default Home;
