import React from "react";
import { motion } from "framer-motion";
import { GiRiceCooker, GiWheat, GiCorn, GiTomato } from "react-icons/gi";

const plants = [
  {
    name: "Rice",
    icon: <GiRiceCooker className="text-green-600 w-16 h-16 mx-auto mb-4" />,
    desc: "Identify rice crops and detect growth stages."
  },
  {
    name: "Wheat",
    icon: <GiWheat className="text-yellow-600 w-16 h-16 mx-auto mb-4" />,
    desc: "Track wheat health and recognize diseases early."
  },
  {
    name: "Corn",
    icon: <GiCorn className="text-orange-600 w-16 h-16 mx-auto mb-4" />,
    desc: "Analyze corn plants for yield prediction."
  },
  {
    name: "Tomato",
    icon: <GiTomato className="text-red-600 w-16 h-16 mx-auto mb-4" />,
    desc: "Detect common tomato diseases and nutrient needs."
  }
];

function Home() {
  return (
    <div className="home">
      {/* Hero Section */}
      <section className="relative bg-green-700 text-white h-[70vh] flex flex-col items-center justify-center text-center px-6">
        <motion.h1
          className="text-5xl font-extrabold mb-4"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          ðŸŒ± Multi-Crop Plant Recognition
        </motion.h1>
        <motion.p
          className="text-lg max-w-xl"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          Smart agriculture powered by AI. Upload images of your crops, detect
          species, track history, and improve yield.
        </motion.p>
        <motion.a
          href="/register"
          className="mt-6 inline-block bg-yellow-400 text-black px-6 py-3 rounded-full font-bold hover:bg-yellow-500 transition"
          whileHover={{ scale: 1.1 }}
        >
          Get Started
        </motion.a>
      </section>

      {/* Plant Features Section */}
      <section className="py-16 bg-gray-100">
        <h2 className="text-3xl font-bold text-center mb-10">
          ðŸŒ¿ Supported Crops
        </h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 px-6 max-w-6xl mx-auto">
          {plants.map((plant, idx) => (
            <motion.div
              key={idx}
              className="bg-white shadow-lg rounded-lg p-6 text-center hover:shadow-xl transition"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 50 }}
              viewport={{ amount: 0.4 }} // ðŸ‘ˆ triggers both on enter and exit
              transition={{ duration: 0.6, delay: idx * 0.2 }}
            >
              {plant.icon}
              <h3 className="font-bold text-lg">{plant.name}</h3>
              <p className="text-sm text-gray-600">{plant.desc}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* Call to Action */}
      <section className="py-20 bg-green-600 text-center text-white">
        <motion.h2
          className="text-3xl font-bold mb-4"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 50 }}
          viewport={{ amount: 0.5 }}
          transition={{ duration: 0.8 }}
        >
          Ready to Transform Your Farming?
        </motion.h2>
        <motion.p
          className="max-w-xl mx-auto mb-6"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          viewport={{ amount: 0.5 }}
          transition={{ delay: 0.3, duration: 1 }}
        >
          Sign up today to access plant recognition, crop history tracking, and
          advanced smart farming tools.
        </motion.p>
        <motion.div whileHover={{ scale: 1.05 }}>
          <a
            href="/register"
            className="bg-yellow-400 text-black px-8 py-3 rounded-full font-bold hover:bg-yellow-500 transition"
          >
            Sign Up Now
          </a>
        </motion.div>
      </section>
    </div>
  );
}

export default Home;
