import React from "react";
import { Link } from "react-router-dom";

function Home() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-green-200 to-green-400">
      {/* Hero */}
      <div className="text-center py-20">
        <h1 className="text-5xl font-extrabold text-green-800 mb-6">
          ðŸŒ± Multi-Crop Plant Recognition
        </h1>
        <p className="text-lg text-gray-700 max-w-2xl mx-auto mb-8">
          Identify crop plants instantly using AI. Get detailed insights, history tracking, 
          and support for multiple plant species.
        </p>
        <div className="space-x-4">
          <Link
            to="/login"
            className="px-6 py-3 bg-green-700 text-white rounded-lg shadow hover:bg-green-800"
          >
            Log In
          </Link>
          <Link
            to="/register"
            className="px-6 py-3 bg-white border border-green-700 text-green-700 rounded-lg shadow hover:bg-gray-100"
          >
            Sign Up
          </Link>
        </div>
      </div>

      {/* Crop Images */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 px-10 pb-10">
        {[
          "https://images.unsplash.com/photo-1598514983315-84e7c5f5d5d3",
          "https://images.unsplash.com/photo-1587731541666-dc6b5c49a82f",
          "https://images.unsplash.com/photo-1504595403659-9088ce801e29",
          "https://images.unsplash.com/photo-1524592094714-0f0654e20314",
        ].map((img, index) => (
          <img
            key={index}
            src={img}
            alt="crop"
            className="rounded-xl shadow-lg object-cover w-full h-64"
          />
        ))}
      </div>
    </div>
  );
}

export default Home;
