import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

function Home() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-green-200 to-green-400">
      {/* Navbar */}
      <nav className="flex justify-between items-center p-6 bg-white shadow-md">
        <h1 className="text-2xl font-bold text-green-700">ðŸŒ± PlantRec</h1>
        <ul className="flex space-x-6 text-lg">
          <li><Link to="/" className="hover:text-green-600">Home</Link></li>
          <li><Link to="/upload" className="hover:text-green-600">Upload</Link></li>
          <li><Link to="/register" className="hover:text-green-600">Register</Link></li>
          <li><Link to="/history" className="hover:text-green-600">History</Link></li>
        </ul>
      </nav>

      {/* Hero Section */}
      <div className="flex flex-col items-center justify-center text-center py-20">
        <motion.h2
          className="text-5xl font-extrabold text-green-800 mb-6"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Discover the World of Plants ðŸŒ¿
        </motion.h2>
        <motion.p
          className="text-lg text-gray-700 max-w-xl mb-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          Upload an image of a plant and let AI recognize its species, common names, and more!
        </motion.p>
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ delay: 1, duration: 0.8 }}
        >
          <Link
            to="/upload"
            className="px-6 py-3 bg-green-600 text-white font-semibold rounded-lg shadow-lg hover:bg-green-700 transition"
          >
            Start Recognizing
          </Link>
        </motion.div>
      </div>

      {/* Plant Images */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 px-10 pb-10">
        {[
          "https://images.unsplash.com/photo-1501004318641-b39e6451bec6",
          "https://images.unsplash.com/photo-1470163395405-d2b80e7450ed",
          "https://images.unsplash.com/photo-1501004318641-b39e6451bec6",
          "https://images.unsplash.com/photo-1524592094714-0f0654e20314",
        ].map((img, index) => (
          <motion.img
            key={index}
            src={img}
            alt="plant"
            className="rounded-2xl shadow-lg object-cover w-full h-64"
            whileHover={{ scale: 1.05 }}
          />
        ))}
      </div>
    </div>
  );
}

export default Home;
